import React, { useState, useEffect } from 'react';
import { questionService } from '../services/api';
import QuestionEditor from './QuestionEditor';

const QuestionList = () => {
  const [questions, setQuestions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [editingQuestion, setEditingQuestion] = useState(null);
  const [error, setError] = useState('');

  useEffect(() => {
    loadQuestions();
  }, []);

  const loadQuestions = async () => {
    try {
      const response = await questionService.getQuestions();
      setQuestions(response.data);
    } catch (error) {
      setError('Error loading questions: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  const handleEdit = (question) => {
    setEditingQuestion(question);
  };

  const handleDelete = async (id) => {
    if (window.confirm('Are you sure you want to delete this question?')) {
      try {
        await questionService.deleteQuestion(id);
        setQuestions(questions.filter(q => q.id !== id));
      } catch (error) {
        setError('Error deleting question: ' + error.message);
      }
    }
  };

  const handleUpdate = async (updatedQuestion) => {
    try {
      await questionService.updateQuestion(updatedQuestion.id, updatedQuestion);
      setQuestions(questions.map(q => 
        q.id === updatedQuestion.id ? updatedQuestion : q
      ));
      setEditingQuestion(null);
    } catch (error) {
      setError('Error updating question: ' + error.message);
    }
  };

  const handleCancelEdit = () => {
    setEditingQuestion(null);
  };

  if (loading) return <div className="text-center">Loading questions...</div>;

  return (
    <div>
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h3>Question Bank</h3>
        <span className="badge bg-primary">{questions.length} questions</span>
      </div>

      {error && (
        <div className="alert alert-danger">{error}</div>
      )}

      {editingQuestion ? (
        <QuestionEditor
          question={editingQuestion}
          onSave={handleUpdate}
          onCancel={handleCancelEdit}
        />
      ) : (
        <div className="row">
          {questions.map((question) => (
            <div key={question.id} className="col-md-6 mb-4">
              <div className="card h-100">
                <div className="card-header d-flex justify-content-between align-items-center">
                  <span className="badge bg-secondary text-uppercase">
                    {question.question_type}
                  </span>
                  <span className="badge bg-info">{question.marks} marks</span>
                </div>
                <div className="card-body">
                  <h6 className="card-title">Question:</h6>
                  <p className="card-text">{question.question_text}</p>
                  
                  {question.image_path && (
                    <div className="mb-3">
                      <img 
                        src={`http://localhost:5000/api/images/${question.image_path.split('/').pop()}`}
                        alt="Question"
                        className="img-fluid"
                        style={{ maxHeight: '200px' }}
                      />
                    </div>
                  )}

                  {question.options && question.options.length > 0 && (
                    <div>
                      <h6>Options:</h6>
                      <ul className="list-unstyled">
                        {question.options.map((option, index) => (
                          <li key={index} className={option.is_correct ? 'text-success fw-bold' : ''}>
                            {option.text} {option.is_correct && '✓'}
                          </li>
                        ))}
                      </ul>
                    </div>
                  )}

                  {question.correct_answer && (
                    <div className="mb-2">
                      <strong>Answer:</strong> {question.correct_answer}
                    </div>
                  )}

                  {question.solution && (
                    <div className="mb-2">
                      <strong>Solution:</strong> {question.solution}
                    </div>
                  )}
                </div>
                <div className="card-footer">
                  <button
                    className="btn btn-sm btn-outline-primary me-2"
                    onClick={() => handleEdit(question)}
                  >
                    Edit
                  </button>
                  <button
                    className="btn btn-sm btn-outline-danger"
                    onClick={() => handleDelete(question.id)}
                  >
                    Delete
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default QuestionList;
