import React, { useState, useRef } from 'react';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';

const QuestionEditor = ({ question, onSave, onCancel }) => {
  const [formData, setFormData] = useState({
    question_text: question.question_text || '',
    question_type: question.question_type || 'multiple_choice',
    marks: question.marks || 1,
    correct_answer: question.correct_answer || '',
    solution: question.solution || '',
    options: question.options || [],
    image_path: question.image_path || ''
  });

  const [activeTab, setActiveTab] = useState('question');
  const fileInputRef = useRef(null);

  // ReactQuill modules configuration
  const modules = {
    toolbar: [
      [{ 'header': [1, 2, 3, 4, 5, 6, false] }],
      [{ 'font': [] }],
      [{ 'size': ['small', false, 'large', 'huge'] }],
      ['bold', 'italic', 'underline', 'strike'],
      [{ 'color': [] }, { 'background': [] }],
      [{ 'script': 'sub'}, { 'script': 'super' }],
      [{ 'list': 'ordered'}, { 'list': 'bullet' }],
      [{ 'indent': '-1'}, { 'indent': '+1' }],
      [{ 'align': [] }],
      ['blockquote', 'code-block'],
      ['link', 'image', 'video'],
      ['clean']
    ],
  };

  const formats = [
    'header', 'font', 'size',
    'bold', 'italic', 'underline', 'strike',
    'color', 'background',
    'script',
    'list', 'bullet', 'indent',
    'align',
    'blockquote', 'code-block',
    'link', 'image', 'video'
  ];

  const getImageUrl = (imagePath) => {
    if (!imagePath) return null;
    const filename = imagePath.split(/[\\/]/).pop();
    return `http://localhost:5000/api/images/${filename}`;
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleRichTextChange = (value, field) => {
    setFormData(prev => ({
      ...prev,
      [field]: value
    }));
  };

  const handleOptionChange = (index, field, value) => {
    const updatedOptions = [...formData.options];
    updatedOptions[index] = {
      ...updatedOptions[index],
      [field]: value
    };
    setFormData(prev => ({
      ...prev,
      options: updatedOptions
    }));
  };

  const handleOptionRichTextChange = (index, value) => {
    const updatedOptions = [...formData.options];
    updatedOptions[index] = {
      ...updatedOptions[index],
      text: value
    };
    setFormData(prev => ({
      ...prev,
      options: updatedOptions
    }));
  };

  const addOption = () => {
    setFormData(prev => ({
      ...prev,
      options: [...prev.options, { text: '', is_correct: false }]
    }));
  };

  const removeOption = (index) => {
    setFormData(prev => ({
      ...prev,
      options: prev.options.filter((_, i) => i !== index)
    }));
  };

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      // Here you would typically upload the image to your server
      // For now, we'll just update the image path
      const newImagePath = `uploads/images/${file.name}`;
      setFormData(prev => ({
        ...prev,
        image_path: newImagePath
      }));
    }
  };

  const triggerImageUpload = () => {
    fileInputRef.current?.click();
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSave({
      ...question,
      ...formData
    });
  };

  const imageUrl = getImageUrl(formData.image_path);

  return (
    <div className="card">
      <div className="card-header">
        <h4>Edit Question</h4>
      </div>
      <div className="card-body">
        <form onSubmit={handleSubmit}>
          {/* Navigation Tabs */}
          <ul className="nav nav-tabs mb-4">
            <li className="nav-item">
              <button
                type="button"
                className={`nav-link ${activeTab === 'question' ? 'active' : ''}`}
                onClick={() => setActiveTab('question')}
              >
                Question
              </button>
            </li>
            <li className="nav-item">
              <button
                type="button"
                className={`nav-link ${activeTab === 'options' ? 'active' : ''}`}
                onClick={() => setActiveTab('options')}
              >
                Options
              </button>
            </li>
            <li className="nav-item">
              <button
                type="button"
                className={`nav-link ${activeTab === 'solution' ? 'active' : ''}`}
                onClick={() => setActiveTab('solution')}
              >
                Solution
              </button>
            </li>
            <li className="nav-item">
              <button
                type="button"
                className={`nav-link ${activeTab === 'settings' ? 'active' : ''}`}
                onClick={() => setActiveTab('settings')}
              >
                Settings
              </button>
            </li>
          </ul>

          {/* Question Tab */}
          {activeTab === 'question' && (
            <div>
              <div className="mb-3">
                <label className="form-label">Question Text</label>
                <ReactQuill
                  value={formData.question_text}
                  onChange={(value) => handleRichTextChange(value, 'question_text')}
                  modules={modules}
                  formats={formats}
                  theme="snow"
                  style={{ height: '200px', marginBottom: '50px' }}
                />
              </div>

              <div className="mb-3">
                <label className="form-label">Attach Image</label>
                <div>
                  <input
                    type="file"
                    ref={fileInputRef}
                    onChange={handleImageUpload}
                    accept="image/*"
                    style={{ display: 'none' }}
                  />
                  <button
                    type="button"
                    className="btn btn-outline-primary btn-sm me-2"
                    onClick={triggerImageUpload}
                  >
                    üìÅ Upload Image
                  </button>
                  {imageUrl && (
                    <div className="mt-2">
                      <img 
                        src={imageUrl}
                        alt="Question illustration"
                        className="img-fluid rounded border"
                        style={{ maxHeight: '150px' }}
                      />
                      <div className="form-text">
                        Current image: {formData.image_path}
                      </div>
                    </div>
                  )}
                </div>
              </div>
            </div>
          )}

          {/* Options Tab */}
          {activeTab === 'options' && (
            <div>
              <div className="mb-3">
                <label className="form-label">Question Type</label>
                <select
                  className="form-select"
                  name="question_type"
                  value={formData.question_type}
                  onChange={handleInputChange}
                >
                  <option value="multiple_choice">Multiple Choice</option>
                  <option value="integer">Integer</option>
                  <option value="fill_ups">Fill in the Blanks</option>
                  <option value="true_false">True/False</option>
                  <option value="comprehension">Comprehension</option>
                </select>
              </div>

              {formData.question_type === 'multiple_choice' && (
                <div className="mb-3">
                  <label className="form-label">Options</label>
                  {formData.options.map((option, index) => (
                    <div key={index} className="card mb-3">
                      <div className="card-header bg-light d-flex justify-content-between align-items-center">
                        <span>Option {index + 1}</span>
                        <div>
                          <div className="form-check form-check-inline">
                            <input
                              type="checkbox"
                              className="form-check-input"
                              checked={option.is_correct}
                              onChange={(e) => handleOptionChange(index, 'is_correct', e.target.checked)}
                            />
                            <label className="form-check-label">Correct</label>
                          </div>
                          <button
                            type="button"
                            className="btn btn-outline-danger btn-sm ms-2"
                            onClick={() => removeOption(index)}
                          >
                            Remove
                          </button>
                        </div>
                      </div>
                      <div className="card-body">
                        <ReactQuill
                          value={option.text}
                          onChange={(value) => handleOptionRichTextChange(index, value)}
                          modules={modules}
                          formats={formats}
                          theme="snow"
                          style={{ height: '120px', marginBottom: '40px' }}
                        />
                      </div>
                    </div>
                  ))}
                  <button
                    type="button"
                    className="btn btn-outline-success btn-sm"
                    onClick={addOption}
                  >
                    + Add Option
                  </button>
                </div>
              )}

              {(formData.question_type === 'integer' || 
                formData.question_type === 'fill_ups' || 
                formData.question_type === 'true_false') && (
                <div className="mb-3">
                  <label className="form-label">Correct Answer</label>
                  <ReactQuill
                    value={formData.correct_answer}
                    onChange={(value) => handleRichTextChange(value, 'correct_answer')}
                    modules={modules}
                    formats={formats}
                    theme="snow"
                    style={{ height: '120px', marginBottom: '40px' }}
                  />
                </div>
              )}
            </div>
          )}

          {/* Solution Tab */}
          {activeTab === 'solution' && (
            <div className="mb-3">
              <label className="form-label">Solution</label>
              <ReactQuill
                value={formData.solution}
                onChange={(value) => handleRichTextChange(value, 'solution')}
                modules={modules}
                formats={formats}
                theme="snow"
                style={{ height: '200px', marginBottom: '50px' }}
              />
            </div>
          )}

          {/* Settings Tab */}
          {activeTab === 'settings' && (
            <div>
              <div className="mb-3">
                <label className="form-label">Marks</label>
                <input
                  type="number"
                  className="form-control"
                  name="marks"
                  value={formData.marks}
                  onChange={handleInputChange}
                  min="1"
                  required
                />
              </div>

              <div className="mb-3">
                <label className="form-label">Question Type</label>
                <select
                  className="form-select"
                  name="question_type"
                  value={formData.question_type}
                  onChange={handleInputChange}
                >
                  <option value="multiple_choice">Multiple Choice</option>
                  <option value="integer">Integer</option>
                  <option value="fill_ups">Fill in the Blanks</option>
                  <option value="true_false">True/False</option>
                  <option value="comprehension">Comprehension</option>
                </select>
              </div>
            </div>
          )}

          {/* Action Buttons */}
          <div className="d-flex gap-2 mt-4 pt-3 border-top">
            <button type="submit" className="btn btn-primary">
              üíæ Save Changes
            </button>
            <button type="button" className="btn btn-secondary" onClick={onCancel}>
              ‚ùå Cancel
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default QuestionEditor;
