import React, { useState, useEffect } from 'react';
import { questionService } from '../services/api';
import QuestionEditor from './QuestionEditor';

const QuestionList = () => {
  const [questions, setQuestions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [editingQuestion, setEditingQuestion] = useState(null);
  const [error, setError] = useState('');

  useEffect(() => {
    loadQuestions();
  }, []);

  const loadQuestions = async () => {
    try {
      const response = await questionService.getQuestions();
      setQuestions(response.data);
    } catch (error) {
      setError('Error loading questions: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  const getImageUrl = (imagePath) => {
    if (!imagePath) return null;
    
    // Extract filename from path (handles both / and \ separators)
    const filename = imagePath.split(/[\\/]/).pop();
    return `http://localhost:5000/api/images/${filename}`;
  };

  const handleEdit = (question) => {
    setEditingQuestion(question);
  };

  const handleDelete = async (id) => {
    if (window.confirm('Are you sure you want to delete this question?')) {
      try {
        await questionService.deleteQuestion(id);
        setQuestions(questions.filter(q => q.id !== id));
      } catch (error) {
        setError('Error deleting question: ' + error.message);
      }
    }
  };

  const handleUpdate = async (updatedQuestion) => {
    try {
      await questionService.updateQuestion(updatedQuestion.id, updatedQuestion);
      setQuestions(questions.map(q => 
        q.id === updatedQuestion.id ? updatedQuestion : q
      ));
      setEditingQuestion(null);
    } catch (error) {
      setError('Error updating question: ' + error.message);
    }
  };

  const handleCancelEdit = () => {
    setEditingQuestion(null);
  };

  if (loading) return <div className="text-center">Loading questions...</div>;

  return (
    <div>
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h3>Question Bank</h3>
        <span className="badge bg-primary">{questions.length} questions</span>
      </div>

      {error && (
        <div className="alert alert-danger">{error}</div>
      )}

      {editingQuestion ? (
        <QuestionEditor
          question={editingQuestion}
          onSave={handleUpdate}
          onCancel={handleCancelEdit}
        />
      ) : (
        <div className="row">
          {questions.map((question) => {
            const imageUrl = getImageUrl(question.image_path);
            
            return (
              <div key={question.id} className="col-md-6 mb-4">
                <div className="card h-100">
                  <div className="card-header d-flex justify-content-between align-items-center">
                    <span className="badge bg-secondary text-uppercase">
                      {question.question_type}
                    </span>
                    <span className="badge bg-info">{question.marks} marks</span>
                  </div>
                  <div className="card-body">
                    <h6 className="card-title">Question:</h6>
                    <p className="card-text" style={{ whiteSpace: 'pre-wrap' }}>
                      {question.question_text}
                    </p>
                    
                    {imageUrl && (
                      <div className="mb-3 text-center">
                        <div className="alert alert-info py-1 mb-2 small">
                          ðŸ“Ž Image attached
                        </div>
                        <img 
                          src={imageUrl}
                          alt="Question illustration"
                          className="img-fluid rounded border"
                          style={{ 
                            maxHeight: '200px', 
                            maxWidth: '100%',
                            objectFit: 'contain' 
                          }}
                          onError={(e) => {
                            e.target.style.display = 'none';
                            e.target.previousSibling.textContent = 'âŒ Image failed to load';
                          }}
                        />
                      </div>
                    )}

                    {question.options && question.options.length > 0 && (
                      <div className="mt-3">
                        <h6>Options:</h6>
                        <div className="list-group">
                          {question.options.map((option, index) => (
                            <div 
                              key={index} 
                              className={`list-group-item ${option.is_correct ? 'list-group-item-success fw-bold' : ''}`}
                            >
                              <div className="d-flex align-items-center">
                                <span className="me-2">{String.fromCharCode(65 + index)}.</span>
                                <span>{option.text}</span>
                                {option.is_correct && (
                                  <span className="badge bg-success ms-2">Correct</span>
                                )}
                              </div>
                            </div>
                          ))}
                        </div>
                      </div>
                    )}

                    {question.correct_answer && (
                      <div className="mt-2">
                        <strong>Answer:</strong> 
                        <span className="badge bg-success ms-2">
                          {question.correct_answer}
                        </span>
                      </div>
                    )}

                    {question.solution && (
                      <div className="mt-2">
                        <strong>Solution:</strong> 
                        <div className="alert alert-light mt-1" style={{ whiteSpace: 'pre-wrap' }}>
                          {question.solution}
                        </div>
                      </div>
                    )}
                  </div>
                  <div className="card-footer">
                    <button
                      className="btn btn-sm btn-outline-primary me-2"
                      onClick={() => handleEdit(question)}
                    >
                      Edit
                    </button>
                    <button
                      className="btn btn-sm btn-outline-danger"
                      onClick={() => handleDelete(question.id)}
                    >
                      Delete
                    </button>
                  </div>
                </div>
              </div>
            );
          })}
        </div>
      )}
    </div>
  );
};

export default QuestionList;
