import React, { useState } from 'react';

const QuestionEditor = ({ question, onSave, onCancel }) => {
  const [formData, setFormData] = useState({
    question_text: question.question_text || '',
    question_type: question.question_type || 'multiple_choice',
    marks: question.marks || 1,
    correct_answer: question.correct_answer || '',
    solution: question.solution || '',
    options: question.options || [],
    image_path: question.image_path || ''
  });

  const getImageUrl = (imagePath) => {
    if (!imagePath) return null;
    const filename = imagePath.split(/[\\/]/).pop();
    return `http://localhost:5000/api/images/${filename}`;
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleOptionChange = (index, field, value) => {
    const updatedOptions = [...formData.options];
    updatedOptions[index] = {
      ...updatedOptions[index],
      [field]: value
    };
    setFormData(prev => ({
      ...prev,
      options: updatedOptions
    }));
  };

  const addOption = () => {
    setFormData(prev => ({
      ...prev,
      options: [...prev.options, { text: '', is_correct: false }]
    }));
  };

  const removeOption = (index) => {
    setFormData(prev => ({
      ...prev,
      options: prev.options.filter((_, i) => i !== index)
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSave({
      ...question,
      ...formData
    });
  };

  const imageUrl = getImageUrl(formData.image_path);

  return (
    <div className="card">
      <div className="card-header">
        <h4>Edit Question</h4>
      </div>
      <div className="card-body">
        <form onSubmit={handleSubmit}>
          <div className="mb-3">
            <label className="form-label">Question Type</label>
            <select
              className="form-select"
              name="question_type"
              value={formData.question_type}
              onChange={handleInputChange}
            >
              <option value="multiple_choice">Multiple Choice</option>
              <option value="integer">Integer</option>
              <option value="fill_ups">Fill in the Blanks</option>
              <option value="true_false">True/False</option>
              <option value="comprehension">Comprehension</option>
            </select>
          </div>

          <div className="mb-3">
            <label className="form-label">Question Text</label>
            <textarea
              className="form-control"
              name="question_text"
              value={formData.question_text}
              onChange={handleInputChange}
              rows="4"
              required
            />
          </div>

          {imageUrl && (
            <div className="mb-3">
              <label className="form-label">Attached Image</label>
              <div>
                <img 
                  src={imageUrl}
                  alt="Question illustration"
                  className="img-fluid rounded border"
                  style={{ maxHeight: '150px' }}
                />
                <div className="form-text">
                  Image: {formData.image_path}
                </div>
              </div>
            </div>
          )}

          <div className="mb-3">
            <label className="form-label">Marks</label>
            <input
              type="number"
              className="form-control"
              name="marks"
              value={formData.marks}
              onChange={handleInputChange}
              min="1"
              required
            />
          </div>

          {formData.question_type === 'multiple_choice' && (
            <div className="mb-3">
              <label className="form-label">Options</label>
              {formData.options.map((option, index) => (
                <div key={index} className="card mb-2">
                  <div className="card-body py-2">
                    <div className="row align-items-center">
                      <div className="col-md-8">
                        <input
                          type="text"
                          className="form-control"
                          placeholder={`Option ${index + 1}`}
                          value={option.text}
                          onChange={(e) => handleOptionChange(index, 'text', e.target.value)}
                        />
                      </div>
                      <div className="col-md-3">
                        <div className="form-check">
                          <input
                            type="checkbox"
                            className="form-check-input"
                            checked={option.is_correct}
                            onChange={(e) => handleOptionChange(index, 'is_correct', e.target.checked)}
                          />
                          <label className="form-check-label">Correct Answer</label>
                        </div>
                      </div>
                      <div className="col-md-1">
                        <button
                          type="button"
                          className="btn btn-outline-danger btn-sm"
                          onClick={() => removeOption(index)}
                        >
                          Ã—
                        </button>
                      </div>
                    </div>
                  </div>
                </div>
              ))}
              <button
                type="button"
                className="btn btn-outline-secondary btn-sm"
                onClick={addOption}
              >
                + Add Option
              </button>
            </div>
          )}

          {(formData.question_type === 'integer' || 
            formData.question_type === 'fill_ups' || 
            formData.question_type === 'true_false') && (
            <div className="mb-3">
              <label className="form-label">Correct Answer</label>
              <input
                type="text"
                className="form-control"
                name="correct_answer"
                value={formData.correct_answer}
                onChange={handleInputChange}
              />
            </div>
          )}

          <div className="mb-3">
            <label className="form-label">Solution</label>
            <textarea
              className="form-control"
              name="solution"
              value={formData.solution}
              onChange={handleInputChange}
              rows="3"
            />
          </div>

          <div className="d-flex gap-2">
            <button type="submit" className="btn btn-primary">
              Save Changes
            </button>
            <button type="button" className="btn btn-secondary" onClick={onCancel}>
              Cancel
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default QuestionEditor;
